INSERT INTO `post` (`id_post`, `title_post`, `image_post`, `book_post`, `content_post`, `summary_post`, `id_category`, `status_post`, `created_at`, `updated_at`) VALUES
(1, 'Thiên thần nhỏ của tôi', '1656158223_thiên thần nhỏ của tôi.jpg', '1656158223_10 cau noi bat hu cua Bill Gates.pdf', '<p>Th&ocirc;ng Tin Về S&aacute;ch</p>\r\n\r\n<p>T&ecirc;n Nh&agrave; Cung Cấp: NXB Trẻ</p>\r\n\r\n<p>T&aacute;c giả: Nguyễn Nhật &Aacute;nh</p>\r\n\r\n<p>NXB: NXB Trẻ</p>\r\n\r\n<p>Năm XB: 2022</p>\r\n\r\n<p>Trọng lượng (gr): 148</p>\r\n\r\n<p>K&iacute;ch Thước Bao B&igrave;: 20 x 13 x 0.7 cm</p>\r\n\r\n<p>Số trang: 134</p>\r\n\r\n<p>Khi dọn về ng&ocirc;i nh&agrave; mới, Kha c&oacute; một c&ocirc; bạn ngh&egrave;o sống trong ng&otilde; hẻm gần đ&oacute;. Khu vườn v&agrave; ng&ocirc;i biệt thự m&agrave; cha mẹ Kha được cấp kh&ocirc;ng ngờ l&agrave; nơi quen thuộc của Hồng Hoa, cứ như c&ocirc; ấy đ&atilde; gắn b&oacute; với n&oacute; từ l&acirc;u rồi. V&igrave; thế, d&ugrave; bị chủ nh&agrave; mới ngăn cấm, c&ocirc; vẫn l&eacute;n l&uacute;t t&igrave;m v&agrave;o khu vườn, để rồi gặp phải tai nạn... V&agrave;&hellip;</p>', '<p>Khi dọn về ng&ocirc;i nh&agrave; mới, Kha c&oacute; một c&ocirc; bạn ngh&egrave;o sống trong ng&otilde; hẻm gần đ&oacute;. Khu vườn v&agrave; ng&ocirc;i biệt thự m&agrave; cha mẹ Kha được cấp kh&ocirc;ng ngờ l&agrave; nơi quen thuộc của Hồng Hoa,..</p>', 1, 1, '2022-06-25 04:57:03', '2022-06-25 04:57:03'),
(2, 'Tư duy nhanh và chậm - Daniel Kahneman', '1656158579_tu-duy-nhanh-va-cham.png', '1656158579_10 cau noi bat hu cua Bill Gates.pdf', '<p>Tư duy nhanh v&agrave; chậm của Daniel Kahneman, cuốn s&aacute;ch t&oacute;m lược h&agrave;ng thập ni&ecirc;n nghi&ecirc;n cứu gi&uacute;p &ocirc;ng đoạt giải Nobel, giải th&iacute;ch những đ&oacute;ng g&oacute;p của &ocirc;ng trong tư duy hiện đại của ta về t&acirc;m l&yacute; v&agrave; kinh tế học h&agrave;nh vi. Trong nhiều năm, Kahneman v&agrave; những đồng nghiệp của m&igrave;nh đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p lớn lao cho lối hiểu biết mới về t&acirc;m tr&iacute; con người. Giờ đ&acirc;y, ta đ&atilde; hiểu s&acirc;u sắc hơn về c&aacute;ch con người ra quyết định, tại sao một số lỗi đ&aacute;nh gi&aacute; lại rất phổ biến v&agrave; l&agrave;m sao để cải thiện bản th&acirc;n tốt hơn.</p>', '<p>Tư duy nhanh v&agrave; chậm của Daniel Kahneman, cuốn s&aacute;ch t&oacute;m lược h&agrave;ng thập ni&ecirc;n nghi&ecirc;n cứu gi&uacute;p &ocirc;ng đoạt giải Nobel, giải th&iacute;ch những đ&oacute;ng g&oacute;p của &ocirc;ng trong tư duy hiện đại của ta về t&acirc;m l&yacute; v&agrave; kinh tế học h&agrave;nh vi. Trong nhiều năm, Kahneman v&agrave; những đồng nghiệp của m&igrave;nh đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p lớn lao cho lối hiểu biết mới về t&acirc;m tr&iacute; con người. Giờ đ&acirc;y, ta đ&atilde; hiểu s&acirc;u sắc hơn về c&aacute;ch con người ra quyết định, tại sao một số lỗi đ&aacute;nh gi&aacute; lại rất phổ biến v&agrave; l&agrave;m sao để cải thiện bản th&acirc;n tốt hơn.</p>', 2, 1, '2022-06-25 05:02:59', '2022-06-25 05:02:59'),
(3, 'Cuộc Sống Vốn Không Công Bằng, Hãy Tập Quen Dần Với Điều Đó', '1656158744_tranh-trich-dan-bill-gates-WB110.jpg', '1656158744_10 cau noi bat hu cua Bill Gates.pdf', '<p>Tranh tr&iacute;ch dẫn Bill Gates &ldquo;Cuộc sống vốn kh&ocirc;ng c&ocirc;ng bằng, h&atilde;y tập quen dần với điều đ&oacute;&rdquo; WB110 l&agrave; một trong những d&ograve;ng sản phẩm nổi bật của Tranh Đẹp Waki. C&ograve;n chần chờ g&igrave; nữa m&agrave; kh&ocirc;ng đặt mua h&agrave;ng ngay để sở hữu tranh để b&agrave;n vừa đẹp vừa &yacute; nghĩa.</p>', '<p>Tranh tr&iacute;ch dẫn Bill Gates &ldquo;Cuộc sống vốn kh&ocirc;ng c&ocirc;ng bằng, h&atilde;y tập quen dần với điều đ&oacute;&rdquo; WB110 l&agrave; một trong những d&ograve;ng sản phẩm nổi bật của Tranh Đẹp Waki. C&ograve;n chần chờ g&igrave; nữa m&agrave; kh&ocirc;ng đặt mua h&agrave;ng ngay để sở hữu tranh để b&agrave;n vừa đẹp vừa &yacute; nghĩa.</p>', 2, 1, '2022-06-25 05:05:44', '2022-06-25 05:05:44'),
(4, 'ĐẮC NHÂN TÂM', '1656158991_dac-nhan-tam-1.jpg', '1656158991_10 cau noi bat hu cua Bill Gates.pdf', '<p>Đối nh&acirc;n xử thế kh&ocirc;ng phải xuất ph&aacute;t từ bản năng của con người m&agrave; mỗi ch&uacute;ng ta đều phải quan s&aacute;t, nh&igrave;n nhận những t&igrave;nh huống để r&uacute;t ra được những kinh nghiệm cho ri&ecirc;ng m&igrave;nh. Đắc nh&acirc;n t&acirc;m ch&iacute;nh l&agrave; một quyển s&aacute;ch m&agrave; bạn cần c&oacute; để biết c&aacute;ch chinh phục người đối diện khi giao tiếp.&nbsp;</p>\r\n\r\n<p>Nội dung s&aacute;ch Đắc nh&acirc;n t&acirc;m c&oacute; g&igrave; th&uacute; vị?<br />\r\nTo&agrave;n bộ nội dung được truyền tải trong 4 phần, tuy nhi&ecirc;n bạn c&oacute; thể nh&igrave;n thấy được nhiều kiến thức, nhiều kh&iacute;a cạnh của hoạt động giao tiếp hằng ng&agrave;y.</p>\r\n\r\n<p>Nội dung s&aacute;ch Đắc nh&acirc;n t&acirc;m c&oacute; g&igrave; th&uacute; vị?<br />\r\nTo&agrave;n bộ nội dung được truyền tải trong 4 phần, tuy nhi&ecirc;n bạn c&oacute; thể nh&igrave;n thấy được nhiều kiến thức, nhiều kh&iacute;a cạnh của hoạt động giao tiếp hằng ng&agrave;y.</p>\r\n\r\n<p>Thứ nhất l&agrave; những nghệ thuật ứng xử cơ bản nhất m&agrave; mỗi ch&uacute;ng ta cần cập nhật</p>\r\n\r\n<p>Thứ hai, 6 c&aacute;ch cần &aacute;p dụng để tạo thiện cảm đối với người kh&aacute;c</p>\r\n\r\n<p>Thứ ba, c&aacute;ch để &ldquo;l&ocirc;i k&eacute;o&rdquo; suy nghĩ người kh&aacute;c theo mong muốn của bạn</p>\r\n\r\n<p>Thứ tư, c&aacute;ch để người kh&aacute;c nh&igrave;n nhận được những điều cần thay đổi của họ một c&aacute;ch dễ d&agrave;ng</p>', '<p>Đối nh&acirc;n xử thế kh&ocirc;ng phải xuất ph&aacute;t từ bản năng của con người m&agrave; mỗi ch&uacute;ng ta đều phải quan s&aacute;t, nh&igrave;n nhận những t&igrave;nh huống để r&uacute;t ra được những kinh nghiệm cho ri&ecirc;ng m&igrave;nh. Đắc nh&acirc;n t&acirc;m ch&iacute;nh l&agrave; một quyển s&aacute;ch m&agrave; bạn cần c&oacute; để biết c&aacute;ch chinh phục người đối diện khi giao tiếp.&nbsp;</p>', 2, 1, '2022-06-25 05:09:51', '2022-06-25 05:09:51'),
(5, 'PHI LÝ TRÍ', '1656184568_Screenshot 2022-06-26 021548.png', '1656159229_10 cau noi bat hu cua Bill Gates.pdf', '<p>ong h&agrave;ng loạt c&aacute;c thực nghiệm nhằm l&agrave;m s&aacute;ng tỏ vấn đề, Dan Ariely, đ&atilde; phản b&aacute;c lại quan điểm chung cho rằng về cơ bản con người lu&ocirc;n h&agrave;nh động dựa tr&ecirc;n l&yacute; tr&iacute;. Bằng sự kết hợp giữa nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u với những trải nghiệm thực tế, Ariely đ&atilde; cho ch&uacute;ng ta một c&acirc;u trả lời bất ngờ: ch&uacute;ng ta đ&ocirc;i khi phi l&yacute; tr&iacute; hơn ch&uacute;ng ta tưởng. Thậm ch&iacute; l&agrave; thường xuy&ecirc;n phi l&yacute; tr&iacute; v&agrave; phi l&yacute; tr&iacute; một c&aacute;ch c&oacute; hệ thống.</p>\r\n\r\n<p>Ariely kh&aacute;m ph&aacute; ra rằng ch&uacute;ng ta kh&ocirc;ng chỉ phạm c&aacute;c lỗi đơn giản h&agrave;ng ng&agrave;y, m&agrave; c&ograve;n lặp lại ch&uacute;ng. Ch&uacute;ng ta kh&ocirc;ng hiểu được t&aacute;c động của x&uacute;c cảm đối với những g&igrave; m&igrave;nh muốn v&agrave; thường đ&aacute;nh gi&aacute; qu&aacute; cao những g&igrave; m&igrave;nh c&oacute;. Nhưng những h&agrave;nh vi sai lầm n&agrave;y kh&ocirc;ng hề ngẫu nhi&ecirc;n hay v&ocirc; nghĩa. Ch&uacute;ng c&oacute; thể được dự đo&aacute;n.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i rằng, Phi l&yacute; tr&iacute; của Dan Ariely l&agrave; một cuốn s&aacute;ch đặc biệt hấp dẫn v&agrave; đầy cảm hứng. N&oacute; buộc người đọc phải suy nghĩ kỹ hơn về tất cả những h&agrave;nh vi, những ai lầm của m&igrave;nh để sống hợp l&yacute; v&agrave; tốt đẹp hơn. V&igrave; thế, kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi cuốn s&aacute;ch vẫn nằm trong danh s&aacute;ch Best-seller d&ugrave; đ&atilde; được xuất bản c&aacute;ch đ&acirc;y một năm.</p>', '<p>Phi l&yacute; tr&iacute; của Dan Ariely l&agrave; một cuốn s&aacute;ch đặc biệt hấp dẫn v&agrave; đầy cảm hứng. N&oacute; buộc người đọc phải suy nghĩ kỹ hơn về tất cả những h&agrave;nh vi, những ai lầm của m&igrave;nh để sống hợp l&yacute; v&agrave; tốt đẹp hơn. V&igrave; thế, kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi cuốn s&aacute;ch vẫn nằm trong danh s&aacute;ch Best-seller d&ugrave; đ&atilde; được xuất bản c&aacute;ch đ&acirc;y một năm.</p>', 2, 1, '2022-06-25 05:13:49', '2022-06-25 12:16:08'),
(6, 'Tâm lý học hài hước – Richard Wiseman', '1656159374_Tam-Ly-Hoc-Hai-Huoc.jpg', '1656159374_10 cau noi bat hu cua Bill Gates.pdf', '<p>Bạn c&ograve;n l&yacute; giải được một số điều trong cuộc sống vốn tưởng rằng đ&oacute; l&agrave; định mệnh. V&iacute; dụ như sự may mắn hay xui xẻo của con người l&agrave; do đ&acirc;u? T&ecirc;n của bạn thật sự c&oacute; phải l&agrave; do sự ngẫu nhi&ecirc;n v&agrave; c&oacute; ảnh hưởng g&igrave; đến bạn hay kh&ocirc;ng?</p>\r\n\r\n<p>Để t&igrave;m hiểu tất cả những th&ocirc;ng tin đơn giản nhưng lại hết sức th&uacute; vị n&agrave;y, bạn h&atilde;y sở hữu ngay quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học h&agrave;i hước&rdquo;.</p>', '<p>L&agrave; một quyển s&aacute;ch t&acirc;m l&yacute; kh&aacute; đặc biệt khi nội dung chủ yếu xoay quanh c&aacute;c vấn đề thường ng&agrave;y trong cuộc sống. D&ugrave; đ&acirc;y l&agrave; những điều thường xuy&ecirc;n được xảy ra nhưng đ&ocirc;i khi bạn cũng chưa l&yacute; giải được. Chẳng hạn, những đặc điểm t&acirc;m l&yacute; khi bạn đang trong qu&aacute; tr&igrave;nh ra quyết định hay một người &iacute;ch kỳ sẽ thường c&oacute; t&acirc;m l&yacute; như thế n&agrave;o?</p>', 2, 1, '2022-06-25 05:16:14', '2022-06-25 05:16:14'),
(7, 'Sức mạnh của thói quen – Charles Duhigg', '1656159423_top-10-sach-tam-ly-1.jpg', '1656159423_10 cau noi bat hu cua Bill Gates.pdf', '<p>Mỗi ch&uacute;ng ta d&ugrave; gi&agrave; hay trẻ, d&ugrave; lớn hay b&eacute; đều c&oacute; những th&oacute;i quen được lặp đi lặp lại hằng ng&agrave;y. Cho d&ugrave; đ&oacute; l&agrave; th&oacute;i quen trong học tập, trong c&ocirc;ng việc hay sinh hoạt. Nhưng những điều n&agrave;y c&oacute; thể t&aacute;c động kh&ocirc;ng &iacute;t đến tương lai của bạn. Nếu bạn l&agrave; một người sở hữu c&aacute;c th&oacute;i quen tốt v&agrave; c&oacute; lợi, bạn dễ d&agrave;ng trở th&agrave;nh một người th&agrave;nh c&ocirc;ng trong tương lai. V&agrave; ngược lại.</p>\r\n\r\n<p>Quyển s&aacute;ch n&agrave;y gi&uacute;p bạn kh&aacute;m ph&aacute; kết quả đ&aacute;ng kinh ngạc từ việc c&aacute;c th&oacute;i quen tốt được h&igrave;nh th&agrave;nh v&agrave; kết hợp với nhau. C&aacute;c th&ocirc;ng tin n&agrave;y được ph&acirc;n t&iacute;ch kh&ocirc;ng chỉ dưới g&oacute;c độ l&agrave; một c&aacute; nh&acirc;n. M&agrave; c&ograve;n c&oacute; cả sức mạnh th&oacute;i quen của một tổ chức hay một doanh nghiệp.</p>', '<p>Mỗi ch&uacute;ng ta d&ugrave; gi&agrave; hay trẻ, d&ugrave; lớn hay b&eacute; đều c&oacute; những th&oacute;i quen được lặp đi lặp lại hằng ng&agrave;y. Cho d&ugrave; đ&oacute; l&agrave; th&oacute;i quen trong học tập, trong c&ocirc;ng việc hay sinh hoạt. Nhưng những điều n&agrave;y c&oacute; thể t&aacute;c động kh&ocirc;ng &iacute;t đến tương lai của bạn. Nếu bạn l&agrave; một người sở hữu c&aacute;c th&oacute;i quen tốt v&agrave; c&oacute; lợi, bạn dễ d&agrave;ng trở th&agrave;nh một người th&agrave;nh c&ocirc;ng trong tương lai. V&agrave; ngược lại.</p>', 2, 1, '2022-06-25 05:17:03', '2022-06-25 05:17:03'),
(8, 'Thay đổi – Bí quyết thay đổi khi thay đổi trở nên khó khăn – Chip & Dan Heath', '1656159480_thay-doi-bi-quyet-thay-doi-tro-nen-kho-khan.jpg', '1656159480_10 cau noi bat hu cua Bill Gates.pdf', '<p>Trong bất kỳ t&igrave;nh huống kh&oacute; khăn n&agrave;o, yếu tố t&acirc;m l&yacute; l&agrave; một yếu tố quan trọng gi&uacute;p bạn vượt qua được. Bởi phụ thuộc v&agrave;o c&aacute;ch bạn đối diện m&agrave; vấn đề sẽ được giải quyết như thế n&agrave;o. Hiểu được điều đ&oacute;, t&aacute;c giả của quyển s&aacute;ch &ldquo;Thay đổi &ndash; b&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn&rdquo; đ&atilde; ph&acirc;n t&iacute;ch 3 c&aacute;c đơn giản để vượt qua kh&oacute; khăn gồm:</p>\r\n\r\n<p>Thứ nhất, t&igrave;m ra những tấm gương đi trước, x&aacute;c định lại mục ti&ecirc;u của m&igrave;nh để &aacute;p dụng đối với t&igrave;nh huống hiện tại.</p>\r\n\r\n<p>Thứ hai, lu&ocirc;n hướng đến những cảm x&uacute;c t&iacute;ch cực v&agrave; t&igrave;m những c&aacute;ch t&aacute;c động tốt đến cảm x&uacute;c.</p>\r\n\r\n<p>Thứ ba, thực hiện một cuộc thay đổi. Bạn cần ph&aacute; vỡ mọi khu&ocirc;n khổ đang tồn tại để bắt đầu với một m&ocirc;i trường mới.</p>\r\n\r\n<p>Bạn c&oacute; thể &aacute;p dụng những t&igrave;nh huống thực tiễn n&agrave;y v&agrave;o ngay trong cuộc sống của ch&iacute;nh m&igrave;nh. Đ&oacute; l&agrave; l&yacute; do quyển s&aacute;ch n&agrave;y rất được ưa chuộng.</p>', '<p>Trong bất kỳ t&igrave;nh huống kh&oacute; khăn n&agrave;o, yếu tố t&acirc;m l&yacute; l&agrave; một yếu tố quan trọng gi&uacute;p bạn vượt qua được. Bởi phụ thuộc v&agrave;o c&aacute;ch bạn đối diện m&agrave; vấn đề sẽ được giải quyết như thế n&agrave;o. Hiểu được điều đ&oacute;, t&aacute;c giả của quyển s&aacute;ch &ldquo;Thay đổi &ndash; b&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn&rdquo; đ&atilde; ph&acirc;n t&iacute;ch 3 c&aacute;c đơn giản để vượt qua kh&oacute; khăn gồm</p>', 2, 1, '2022-06-25 05:18:00', '2022-06-25 05:18:00'),
(9, 'Tâm lý học đám đông – Gustave Le Bon', '1656160652_6ba647e2287887ba01a085ced5648c59.jpg', '1656159535_top-10-sach-tam-ly-2.jpg', '<p>T&acirc;m l&yacute; của con người thường c&oacute; xu hướng bị ảnh hưởng bởi đ&aacute;m đ&ocirc;ng. Nhưng ở đ&acirc;y quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng&rdquo; ph&acirc;n t&iacute;ch chiều hướng t&acirc;m l&yacute; n&agrave;y theo qu&aacute; tr&igrave;nh ph&aacute;t triển của một d&acirc;n tộc, một quốc gia. M&agrave; trong đ&oacute;, mỗi con người chịu một sự chi phối từ d&acirc;n tộc theo một c&aacute;ch v&ocirc; thức n&agrave;o đ&oacute;.</p>\r\n\r\n<p>Theo t&aacute;c giả, t&acirc;m l&yacute; của một đ&aacute;m đ&ocirc;ng bị t&aacute;c động bởi những yếu tố kh&aacute;ch quan b&ecirc;n ngo&agrave;i. Đ&oacute; l&agrave; l&yacute; do m&agrave; những đ&aacute;m đ&ocirc;ng n&agrave;y thường c&oacute; những t&acirc;m l&yacute; kh&ocirc;ng ổn định, dễ thay đổi cũng như h&agrave;nh động một c&aacute;ch v&ocirc; thức. Bạn c&oacute; thể kh&aacute;m ph&aacute; th&ecirc;m những h&igrave;nh th&aacute;i t&acirc;m l&yacute; đ&aacute;m đ&ocirc;ng trong quyển s&aacute;ch n&agrave;y.</p>', '<p>T&acirc;m l&yacute; của con người thường c&oacute; xu hướng bị ảnh hưởng bởi đ&aacute;m đ&ocirc;ng. Nhưng ở đ&acirc;y quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng&rdquo; ph&acirc;n t&iacute;ch chiều hướng t&acirc;m l&yacute; n&agrave;y theo qu&aacute; tr&igrave;nh ph&aacute;t triển của một d&acirc;n tộc, một quốc gia. M&agrave; trong đ&oacute;, mỗi con người chịu một sự chi phối từ d&acirc;n tộc theo một c&aacute;ch v&ocirc; thức n&agrave;o đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>', 2, 1, '2022-06-25 05:18:55', '2022-06-25 05:37:32'),
(10, '13 phát ngôn bất hủ của Steve Job', '1656159667_13-phat-ngon-bat-hu-cua-steve-jobs-luc-sinh-thoi.jpg', '1656159667_13 phát ngôn bất hủ của Steven jobs.pdf', '<p>Về c&aacute;i chết, Steve Jobs quan niệm: &ldquo;C&aacute;i chết l&agrave; ph&aacute;t minh vĩ đại nhất của sự sống. N&oacute; l&agrave; t&aacute;c nh&acirc;n thay đổi cuộc sống. N&oacute; loại bỏ sự cũ kỹ để mở đường cho c&aacute;i mới&rdquo;.</p>\r\n\r\n<p><br />\r\nHuyền thoại của Apple đ&atilde; vĩnh viễn ra đi ở tuổi 56, mang lại nỗi tiếc nuối v&ocirc; hạn cho những người y&ecirc;u c&ocirc;ng nghệ tr&ecirc;n to&agrave;n thế giới. L&uacute;c sinh thời, &ocirc;ng lu&ocirc;n &ldquo;ch&aacute;y&rdquo; hết m&igrave;nh cho c&ocirc;ng việc. Phương ch&acirc;m &ldquo;sống l&agrave; kh&ocirc;ng chờ đợi&rdquo; được thể hiện r&otilde; qua những ph&aacute;t biểu của &ocirc;ng về tất cả c&aacute;c lĩnh vực li&ecirc;n quan đến c&ocirc;ng việc, sự s&aacute;ng tạo hay c&aacute;i chết.</p>', '<p>Về c&aacute;i chết, Steve Jobs quan niệm: &ldquo;C&aacute;i chết l&agrave; ph&aacute;t minh vĩ đại nhất của sự sống. N&oacute; l&agrave; t&aacute;c nh&acirc;n thay đổi cuộc sống. N&oacute; loại bỏ sự cũ kỹ để mở đường cho c&aacute;i mới&rdquo;.<br />\r\nHuyền thoại của Apple đ&atilde; vĩnh viễn ra đi ở tuổi 56, mang lại nỗi tiếc nuối v&ocirc; hạn cho những người y&ecirc;u c&ocirc;ng nghệ tr&ecirc;n to&agrave;n thế giới. L&uacute;c sinh thời, &ocirc;ng lu&ocirc;n &ldquo;ch&aacute;y&rdquo; hết m&igrave;nh cho c&ocirc;ng việc. Phương ch&acirc;m &ldquo;sống l&agrave; kh&ocirc;ng chờ đợi&rdquo; được thể hiện r&otilde; qua những ph&aacute;t biểu của &ocirc;ng về tất cả c&aacute;c lĩnh vực li&ecirc;n quan đến c&ocirc;ng việc, sự s&aacute;ng tạo hay c&aacute;i chết.</p>', 2, 1, '2022-06-25 05:21:07', '2022-06-25 05:21:07'),
(11, 'Chúng ta quyết định như thế nào? – Jonah Lehrer', '1656159712_chung-ta-quyet-dinh-nhu-the-nao.jpg', '1656159712_13 phát ngôn bất hủ của Steven jobs.pdf', '<p>Mỗi ng&agrave;y trong cuộc sống ch&uacute;ng ta phải đưa ra rất nhiều c&aacute;c quyết định kh&aacute;c nhau, từ những quyết định đơn giản như ăn g&igrave;?, mặc g&igrave;?, &hellip; cho đến c&aacute;c quyết định quan trọng trong c&ocirc;ng việc. Nhưng bạn c&oacute; biết khi đ&oacute; n&atilde;o đ&atilde; xử l&yacute; v&agrave; hoạt động như thế n&agrave;o để t&aacute;c động đến h&agrave;nh vi ra quyết định n&agrave;y?</p>\r\n\r\n<p>Ch&uacute;ng ta quyết định như thế n&agrave;o?</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta quyết định như thế n&agrave;o&rdquo; cung cấp một c&aacute;c đầy đủ nhất những minh họa thực tế về vấn đề n&agrave;y. Th&ocirc;ng qua đ&oacute;, bạn sẽ biết được một sự thật kinh ngạc l&agrave; đ&ocirc;i khi cảm x&uacute;c sẽ gi&uacute;p bạn đưa ra được một quyết định s&aacute;ng suốt.</p>', '<p>Mỗi ng&agrave;y trong cuộc sống ch&uacute;ng ta phải đưa ra rất nhiều c&aacute;c quyết định kh&aacute;c nhau, từ những quyết định đơn giản như ăn g&igrave;?, mặc g&igrave;?, &hellip; cho đến c&aacute;c quyết định quan trọng trong c&ocirc;ng việc. Nhưng bạn c&oacute; biết khi đ&oacute; n&atilde;o đ&atilde; xử l&yacute; v&agrave; hoạt động như thế n&agrave;o để t&aacute;c động đến h&agrave;nh vi ra quyết định n&agrave;y?</p>\r\n\r\n<p>&nbsp;</p>', 2, 1, '2022-06-25 05:21:52', '2022-06-25 05:21:52'),
(13, 'Đọc vị bất kỳ ai – TS. David J.Lieberman', '1656160806_doc-vi-bat-ky-ai.jpeg', '1656160806_5S Bi Mat Thanh Cong Tu Nhat Ban.PDF', '<p>S&Aacute;CH HAY N&Ecirc;N ĐỌC<br />\r\nTop 10 cuốn s&aacute;ch t&acirc;m l&yacute; học hay nhất mọi thời đại<br />\r\nT&acirc;m l&yacute; con người tưởng chừng l&agrave; một yếu tố rất kh&oacute; đo&aacute;n v&agrave; đ&ograve;i hỏi sự nhanh nhạy để thấu hiểu. Tuy nhi&ecirc;n, với việc trao dồi c&aacute;c kiến thức xoay quanh chủ đề t&acirc;m l&yacute; học n&agrave;y sẽ gi&uacute;p bạn c&oacute; th&ecirc;m những kinh nghiệm cho bản th&acirc;n. S&aacute;ch t&acirc;m l&yacute; học kh&ocirc;ng chỉ gi&uacute;p bạn hiểu được t&acirc;m l&yacute; của người đối phương m&agrave; c&ograve;n c&oacute; thể gi&uacute;p bạn nắm bắt nhanh ch&oacute;ng hơn c&aacute;c kỹ năng về giao tiếp. Nếu bạn định hướng trở th&agrave;nh người l&atilde;nh đạo, th&igrave; c&aacute;c kiến thức n&agrave;y c&ograve;n gi&uacute;p bạn quản l&yacute; được nh&acirc;n sự một c&aacute;ch tốt hơn.</p>\r\n\r\n<p>Review S&aacute;ch: &#39;&#39;Dựa V&agrave;o Ch&iacute;nh M&igrave;nh Để Th&agrave;nh C&ocirc;ng&#39;&#39;<br />\r\nReview S&aacute;ch 21 Quy Tắc Cơ Bản Để Quản L&yacute; Thời Gian<br />\r\nCửa Hiệu Triết Học - Gh&eacute; thăm những gian h&agrave;ng của sự suy tưởng<br />\r\n1. T&acirc;m l&yacute; học h&agrave;i hước &ndash; Richard Wiseman<br />\r\nĐ&acirc;y l&agrave; một quyển s&aacute;ch t&acirc;m l&yacute; kh&aacute; đặc biệt khi nội dung chủ yếu xoay quanh c&aacute;c vấn đề thường ng&agrave;y trong cuộc sống. D&ugrave; đ&acirc;y l&agrave; những điều thường xuy&ecirc;n được xảy ra nhưng đ&ocirc;i khi bạn cũng chưa l&yacute; giải được. Chẳng hạn, những đặc điểm t&acirc;m l&yacute; khi bạn đang trong qu&aacute; tr&igrave;nh ra quyết định hay một người &iacute;ch kỳ sẽ thường c&oacute; t&acirc;m l&yacute; như thế n&agrave;o?</p>\r\n\r\n<p>T&acirc;m l&yacute; học h&agrave;i hước &ndash; Richard Wiseman</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Bạn c&ograve;n l&yacute; giải được một số điều trong cuộc sống vốn tưởng rằng đ&oacute; l&agrave; định mệnh. V&iacute; dụ như sự may mắn hay xui xẻo của con người l&agrave; do đ&acirc;u? T&ecirc;n của bạn thật sự c&oacute; phải l&agrave; do sự ngẫu nhi&ecirc;n v&agrave; c&oacute; ảnh hưởng g&igrave; đến bạn hay kh&ocirc;ng?</p>\r\n\r\n<p>Để t&igrave;m hiểu tất cả những th&ocirc;ng tin đơn giản nhưng lại hết sức th&uacute; vị n&agrave;y, bạn h&atilde;y sở hữu ngay quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học h&agrave;i hước&rdquo;.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 10 cuốn s&aacute;ch hay nhất mọi thời đại m&agrave; bạn n&ecirc;n đọc</p>\r\n\r\n<p>2. Sức mạnh của th&oacute;i quen &ndash; Charles Duhigg<br />\r\nMỗi ch&uacute;ng ta d&ugrave; gi&agrave; hay trẻ, d&ugrave; lớn hay b&eacute; đều c&oacute; những th&oacute;i quen được lặp đi lặp lại hằng ng&agrave;y. Cho d&ugrave; đ&oacute; l&agrave; th&oacute;i quen trong học tập, trong c&ocirc;ng việc hay sinh hoạt. Nhưng những điều n&agrave;y c&oacute; thể t&aacute;c động kh&ocirc;ng &iacute;t đến tương lai của bạn. Nếu bạn l&agrave; một người sở hữu c&aacute;c th&oacute;i quen tốt v&agrave; c&oacute; lợi, bạn dễ d&agrave;ng trở th&agrave;nh một người th&agrave;nh c&ocirc;ng trong tương lai. V&agrave; ngược lại.</p>\r\n\r\n<p>Sức mạnh của th&oacute;i quen &ndash; Charles Duhigg</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Quyển s&aacute;ch n&agrave;y gi&uacute;p bạn kh&aacute;m ph&aacute; kết quả đ&aacute;ng kinh ngạc từ việc c&aacute;c th&oacute;i quen tốt được h&igrave;nh th&agrave;nh v&agrave; kết hợp với nhau. C&aacute;c th&ocirc;ng tin n&agrave;y được ph&acirc;n t&iacute;ch kh&ocirc;ng chỉ dưới g&oacute;c độ l&agrave; một c&aacute; nh&acirc;n. M&agrave; c&ograve;n c&oacute; cả sức mạnh th&oacute;i quen của một tổ chức hay một doanh nghiệp.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 5 cuốn s&aacute;ch ph&aacute;t triển bản th&acirc;n n&ecirc;n đọc một lần trong đời</p>\r\n\r\n<p>3. Thay đổi &ndash; B&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn &ndash; Chip &amp; Dan Heath<br />\r\nTrong bất kỳ t&igrave;nh huống kh&oacute; khăn n&agrave;o, yếu tố t&acirc;m l&yacute; l&agrave; một yếu tố quan trọng gi&uacute;p bạn vượt qua được. Bởi phụ thuộc v&agrave;o c&aacute;ch bạn đối diện m&agrave; vấn đề sẽ được giải quyết như thế n&agrave;o. Hiểu được điều đ&oacute;, t&aacute;c giả của quyển s&aacute;ch &ldquo;Thay đổi &ndash; b&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn&rdquo; đ&atilde; ph&acirc;n t&iacute;ch 3 c&aacute;c đơn giản để vượt qua kh&oacute; khăn gồm:</p>\r\n\r\n<p>Thứ nhất, t&igrave;m ra những tấm gương đi trước, x&aacute;c định lại mục ti&ecirc;u của m&igrave;nh để &aacute;p dụng đối với t&igrave;nh huống hiện tại.</p>\r\n\r\n<p>Thứ hai, lu&ocirc;n hướng đến những cảm x&uacute;c t&iacute;ch cực v&agrave; t&igrave;m những c&aacute;ch t&aacute;c động tốt đến cảm x&uacute;c.</p>\r\n\r\n<p>Thứ ba, thực hiện một cuộc thay đổi. Bạn cần ph&aacute; vỡ mọi khu&ocirc;n khổ đang tồn tại để bắt đầu với một m&ocirc;i trường mới.</p>\r\n\r\n<p>Bạn c&oacute; thể &aacute;p dụng những t&igrave;nh huống thực tiễn n&agrave;y v&agrave;o ngay trong cuộc sống của ch&iacute;nh m&igrave;nh. Đ&oacute; l&agrave; l&yacute; do quyển s&aacute;ch n&agrave;y rất được ưa chuộng.</p>\r\n\r\n<p>Thay đổi - B&iacute; quyết thay đổi khi thay đổi trở l&ecirc;n kh&oacute; khăn</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Xem th&ecirc;m: 10 cuốn s&aacute;ch truyền cảm hứng l&agrave;m gi&agrave;u hay nhất mọi thời đại</p>\r\n\r\n<p>4. T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng &ndash; Gustave Le Bon<br />\r\nT&acirc;m l&yacute; của con người thường c&oacute; xu hướng bị ảnh hưởng bởi đ&aacute;m đ&ocirc;ng. Nhưng ở đ&acirc;y quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng&rdquo; ph&acirc;n t&iacute;ch chiều hướng t&acirc;m l&yacute; n&agrave;y theo qu&aacute; tr&igrave;nh ph&aacute;t triển của một d&acirc;n tộc, một quốc gia. M&agrave; trong đ&oacute;, mỗi con người chịu một sự chi phối từ d&acirc;n tộc theo một c&aacute;ch v&ocirc; thức n&agrave;o đ&oacute;.</p>\r\n\r\n<p>T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng &ndash; Gustave Le Bon</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Theo t&aacute;c giả, t&acirc;m l&yacute; của một đ&aacute;m đ&ocirc;ng bị t&aacute;c động bởi những yếu tố kh&aacute;ch quan b&ecirc;n ngo&agrave;i. Đ&oacute; l&agrave; l&yacute; do m&agrave; những đ&aacute;m đ&ocirc;ng n&agrave;y thường c&oacute; những t&acirc;m l&yacute; kh&ocirc;ng ổn định, dễ thay đổi cũng như h&agrave;nh động một c&aacute;ch v&ocirc; thức. Bạn c&oacute; thể kh&aacute;m ph&aacute; th&ecirc;m những h&igrave;nh th&aacute;i t&acirc;m l&yacute; đ&aacute;m đ&ocirc;ng trong quyển s&aacute;ch n&agrave;y.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 6 cuốn s&aacute;ch thay đổi th&oacute;i quen để tạo dựng th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>5. Ch&uacute;ng ta quyết định như thế n&agrave;o? &ndash; Jonah Lehrer<br />\r\nMỗi ng&agrave;y trong cuộc sống ch&uacute;ng ta phải đưa ra rất nhiều c&aacute;c quyết định kh&aacute;c nhau, từ những quyết định đơn giản như ăn g&igrave;?, mặc g&igrave;?, &hellip; cho đến c&aacute;c quyết định quan trọng trong c&ocirc;ng việc. Nhưng bạn c&oacute; biết khi đ&oacute; n&atilde;o đ&atilde; xử l&yacute; v&agrave; hoạt động như thế n&agrave;o để t&aacute;c động đến h&agrave;nh vi ra quyết định n&agrave;y?</p>\r\n\r\n<p>Ch&uacute;ng ta quyết định như thế n&agrave;o?</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta quyết định như thế n&agrave;o&rdquo; cung cấp một c&aacute;c đầy đủ nhất những minh họa thực tế về vấn đề n&agrave;y. Th&ocirc;ng qua đ&oacute;, bạn sẽ biết được một sự thật kinh ngạc l&agrave; đ&ocirc;i khi cảm x&uacute;c sẽ gi&uacute;p bạn đưa ra được một quyết định s&aacute;ng suốt.</p>\r\n\r\n<p>Xem th&ecirc;m: 8 cuốn s&aacute;ch hay về kinh doanh đ&aacute;ng đọc nhất để th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>6. Những đ&ograve;n t&acirc;m l&yacute; thuyết phục - &nbsp;Robert B.Cialdini<br />\r\nKiểm so&aacute;t được cảm x&uacute;c của bản th&acirc;n cũng như nắm bắt được t&acirc;m l&yacute; của đối phương ch&iacute;nh l&agrave; một vũ kh&iacute; mạnh mẽ để bạn chiến thắng trong c&aacute;c hoạt động đối nh&acirc;n xử thế hằng ng&agrave;y. Nhất l&agrave; trong qu&aacute; tr&igrave;nh thuyết phục người kh&aacute;c. Với những đ&ograve;n t&acirc;m l&yacute; mạnh mẽ được gợi &yacute; từ quyển s&aacute;ch, bạn sẽ kh&ocirc;ng khỏi ngạc nhi&ecirc;n về những hiệu quả m&agrave; n&oacute; mang lại.</p>\r\n\r\n<p>Những đ&ograve;n t&acirc;m l&yacute; thuyết phục - &nbsp;Robert B.Cialdini</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Th&agrave;nh thạo về những thủ thuật t&acirc;m l&yacute; n&agrave;y, bạn chắc chắn sẽ lu&ocirc;n l&agrave;m chủ được cuộc chơi v&agrave; c&agrave;ng ng&agrave;y c&agrave;ng tạo n&ecirc;n khả năng g&acirc;y ảnh hưởng l&ecirc;n t&acirc;m l&yacute; của người kh&aacute;c.</p>\r\n\r\n<p>7. Bức x&uacute;c kh&ocirc;ng l&agrave;m ta v&ocirc; can &ndash; Đặng Ho&agrave;ng Giang<br />\r\nĐ&acirc;y được xem như l&agrave; một quyển s&aacute;ch gi&uacute;p &ldquo;lột trần&rdquo; những sự thật th&uacute; vị từ c&aacute;c sự việc th&acirc;n thuộc trong cuộc sống như c&acirc;u like, phẫu thuật thẩm mĩ, ăn thịt ch&oacute;, &hellip; Một g&oacute;c nh&igrave;n mới v&agrave; từ c&aacute;ch ph&acirc;n t&iacute;ch hấp dẫn của t&aacute;c giả sẽ gi&uacute;p bạn ngộ ra những điều chưa từng nghĩ đến li&ecirc;n quan đến c&aacute;c chủ đề th&acirc;n thuộc nhất.</p>\r\n\r\n<p>Bức x&uacute;c kh&ocirc;ng l&agrave;m ta v&ocirc; can &ndash; Đặng Ho&agrave;ng Giang</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Từ c&aacute;c vấn đề nhỏ n&agrave;y, t&aacute;c giả c&ograve;n kh&eacute;o l&eacute;o n&acirc;ng ph&acirc;n t&iacute;ch của m&igrave;nh l&ecirc;n tầm vĩ m&ocirc; hơn. Đồng thời cung cấp c&aacute;c giải ph&aacute;p để giải quyết c&aacute;c vấn đề n&agrave;y.</p>\r\n\r\n<p>8. Phi l&yacute; tr&iacute; &ndash; Dan Ariely<br />\r\nMỗi ch&uacute;ng ta thường ra quyết định trước những vấn đề trong cuộc sống m&agrave; kh&ocirc;ng theo một nguy&ecirc;n tắc n&agrave;o cả. Khi đ&oacute;, trong t&acirc;m l&yacute; học gọi l&agrave; phi l&yacute; tr&iacute;. V&agrave; quyển s&aacute;ch n&agrave;y được ra đời để l&agrave;m g&igrave;?</p>\r\n\r\n<p>Phi l&yacute; tr&iacute; &ndash; Dan Ariely</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>T&aacute;c giả Dan Ariely mong muốn mỗi đọc giả với những nội dung được quyển s&aacute;ch cung cấp sẽ biết c&aacute;ch đ&aacute;nh gi&aacute; lại những quyết định m&agrave; bạn đ&atilde; đưa ra. Những quyết định n&agrave;y đ&atilde; đ&uacute;ng đắn hay chưa v&agrave; c&oacute; g&igrave; cần sửa đổi? Khi đ&oacute;, bạn sẽ c&oacute; được những b&agrave;i học qu&yacute; b&aacute;u.</p>\r\n\r\n<p>9. Đọc vị bất kỳ ai &ndash; TS. David J.Lieberman<br />\r\nBạn c&oacute; từng muốn biết suy nghĩ của một ai đ&oacute; nhưng nghĩ rằng điều đ&oacute; l&agrave; kh&ocirc;ng thể. Tuy nhi&ecirc;n, bạn sẽ t&igrave;m thấy được c&aacute;c bước để thấu hiểu được ai đ&oacute; đang nghĩ g&igrave; trong những t&igrave;nh huống cụ thể. V&agrave; bạn sẽ &aacute;p dụng được trong những ho&agrave;n cảnh đơn giản. Chẳng hạn như muốn biết một ai đ&oacute; c&oacute; n&ecirc;n tin cậy hay kh&ocirc;ng, hay muốn biết một người trước đ&oacute; chưa từng gặp c&oacute; giống với những g&igrave; bạn tưởng tượng hay kh&ocirc;ng, &hellip; Bạn ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m được nhờ v&agrave;o những nội dung đắt gi&aacute; m&agrave; &ldquo;Đọc vị bất kỳ ai cung cấp&rdquo;.</p>', '<p>S&Aacute;CH HAY N&Ecirc;N ĐỌC<br />\r\nTop 10 cuốn s&aacute;ch t&acirc;m l&yacute; học hay nhất mọi thời đại<br />\r\nT&acirc;m l&yacute; con người tưởng chừng l&agrave; một yếu tố rất kh&oacute; đo&aacute;n v&agrave; đ&ograve;i hỏi sự nhanh nhạy để thấu hiểu. Tuy nhi&ecirc;n, với việc trao dồi c&aacute;c kiến thức xoay quanh chủ đề t&acirc;m l&yacute; học n&agrave;y sẽ gi&uacute;p bạn c&oacute; th&ecirc;m những kinh nghiệm cho bản th&acirc;n. S&aacute;ch t&acirc;m l&yacute; học kh&ocirc;ng chỉ gi&uacute;p bạn hiểu được t&acirc;m l&yacute; của người đối phương m&agrave; c&ograve;n c&oacute; thể gi&uacute;p bạn nắm bắt nhanh ch&oacute;ng hơn c&aacute;c kỹ năng về giao tiếp. Nếu bạn định hướng trở th&agrave;nh người l&atilde;nh đạo, th&igrave; c&aacute;c kiến thức n&agrave;y c&ograve;n gi&uacute;p bạn quản l&yacute; được nh&acirc;n sự một c&aacute;ch tốt hơn.</p>\r\n\r\n<p>Review S&aacute;ch: &#39;&#39;Dựa V&agrave;o Ch&iacute;nh M&igrave;nh Để Th&agrave;nh C&ocirc;ng&#39;&#39;<br />\r\nReview S&aacute;ch 21 Quy Tắc Cơ Bản Để Quản L&yacute; Thời Gian<br />\r\nCửa Hiệu Triết Học - Gh&eacute; thăm những gian h&agrave;ng của sự suy tưởng<br />\r\n1. T&acirc;m l&yacute; học h&agrave;i hước &ndash; Richard Wiseman<br />\r\nĐ&acirc;y l&agrave; một quyển s&aacute;ch t&acirc;m l&yacute; kh&aacute; đặc biệt khi nội dung chủ yếu xoay quanh c&aacute;c vấn đề thường ng&agrave;y trong cuộc sống. D&ugrave; đ&acirc;y l&agrave; những điều thường xuy&ecirc;n được xảy ra nhưng đ&ocirc;i khi bạn cũng chưa l&yacute; giải được. Chẳng hạn, những đặc điểm t&acirc;m l&yacute; khi bạn đang trong qu&aacute; tr&igrave;nh ra quyết định hay một người &iacute;ch kỳ sẽ thường c&oacute; t&acirc;m l&yacute; như thế n&agrave;o?</p>\r\n\r\n<p>T&acirc;m l&yacute; học h&agrave;i hước &ndash; Richard Wiseman</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Bạn c&ograve;n l&yacute; giải được một số điều trong cuộc sống vốn tưởng rằng đ&oacute; l&agrave; định mệnh. V&iacute; dụ như sự may mắn hay xui xẻo của con người l&agrave; do đ&acirc;u? T&ecirc;n của bạn thật sự c&oacute; phải l&agrave; do sự ngẫu nhi&ecirc;n v&agrave; c&oacute; ảnh hưởng g&igrave; đến bạn hay kh&ocirc;ng?</p>\r\n\r\n<p>Để t&igrave;m hiểu tất cả những th&ocirc;ng tin đơn giản nhưng lại hết sức th&uacute; vị n&agrave;y, bạn h&atilde;y sở hữu ngay quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học h&agrave;i hước&rdquo;.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 10 cuốn s&aacute;ch hay nhất mọi thời đại m&agrave; bạn n&ecirc;n đọc</p>\r\n\r\n<p>2. Sức mạnh của th&oacute;i quen &ndash; Charles Duhigg<br />\r\nMỗi ch&uacute;ng ta d&ugrave; gi&agrave; hay trẻ, d&ugrave; lớn hay b&eacute; đều c&oacute; những th&oacute;i quen được lặp đi lặp lại hằng ng&agrave;y. Cho d&ugrave; đ&oacute; l&agrave; th&oacute;i quen trong học tập, trong c&ocirc;ng việc hay sinh hoạt. Nhưng những điều n&agrave;y c&oacute; thể t&aacute;c động kh&ocirc;ng &iacute;t đến tương lai của bạn. Nếu bạn l&agrave; một người sở hữu c&aacute;c th&oacute;i quen tốt v&agrave; c&oacute; lợi, bạn dễ d&agrave;ng trở th&agrave;nh một người th&agrave;nh c&ocirc;ng trong tương lai. V&agrave; ngược lại.</p>\r\n\r\n<p>Sức mạnh của th&oacute;i quen &ndash; Charles Duhigg</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Quyển s&aacute;ch n&agrave;y gi&uacute;p bạn kh&aacute;m ph&aacute; kết quả đ&aacute;ng kinh ngạc từ việc c&aacute;c th&oacute;i quen tốt được h&igrave;nh th&agrave;nh v&agrave; kết hợp với nhau. C&aacute;c th&ocirc;ng tin n&agrave;y được ph&acirc;n t&iacute;ch kh&ocirc;ng chỉ dưới g&oacute;c độ l&agrave; một c&aacute; nh&acirc;n. M&agrave; c&ograve;n c&oacute; cả sức mạnh th&oacute;i quen của một tổ chức hay một doanh nghiệp.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 5 cuốn s&aacute;ch ph&aacute;t triển bản th&acirc;n n&ecirc;n đọc một lần trong đời</p>\r\n\r\n<p>3. Thay đổi &ndash; B&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn &ndash; Chip &amp; Dan Heath<br />\r\nTrong bất kỳ t&igrave;nh huống kh&oacute; khăn n&agrave;o, yếu tố t&acirc;m l&yacute; l&agrave; một yếu tố quan trọng gi&uacute;p bạn vượt qua được. Bởi phụ thuộc v&agrave;o c&aacute;ch bạn đối diện m&agrave; vấn đề sẽ được giải quyết như thế n&agrave;o. Hiểu được điều đ&oacute;, t&aacute;c giả của quyển s&aacute;ch &ldquo;Thay đổi &ndash; b&iacute; quyết thay đổi khi thay đổi trở n&ecirc;n kh&oacute; khăn&rdquo; đ&atilde; ph&acirc;n t&iacute;ch 3 c&aacute;c đơn giản để vượt qua kh&oacute; khăn gồm:</p>\r\n\r\n<p>Thứ nhất, t&igrave;m ra những tấm gương đi trước, x&aacute;c định lại mục ti&ecirc;u của m&igrave;nh để &aacute;p dụng đối với t&igrave;nh huống hiện tại.</p>\r\n\r\n<p>Thứ hai, lu&ocirc;n hướng đến những cảm x&uacute;c t&iacute;ch cực v&agrave; t&igrave;m những c&aacute;ch t&aacute;c động tốt đến cảm x&uacute;c.</p>\r\n\r\n<p>Thứ ba, thực hiện một cuộc thay đổi. Bạn cần ph&aacute; vỡ mọi khu&ocirc;n khổ đang tồn tại để bắt đầu với một m&ocirc;i trường mới.</p>\r\n\r\n<p>Bạn c&oacute; thể &aacute;p dụng những t&igrave;nh huống thực tiễn n&agrave;y v&agrave;o ngay trong cuộc sống của ch&iacute;nh m&igrave;nh. Đ&oacute; l&agrave; l&yacute; do quyển s&aacute;ch n&agrave;y rất được ưa chuộng.</p>\r\n\r\n<p>Thay đổi - B&iacute; quyết thay đổi khi thay đổi trở l&ecirc;n kh&oacute; khăn</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Xem th&ecirc;m: 10 cuốn s&aacute;ch truyền cảm hứng l&agrave;m gi&agrave;u hay nhất mọi thời đại</p>\r\n\r\n<p>4. T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng &ndash; Gustave Le Bon<br />\r\nT&acirc;m l&yacute; của con người thường c&oacute; xu hướng bị ảnh hưởng bởi đ&aacute;m đ&ocirc;ng. Nhưng ở đ&acirc;y quyển s&aacute;ch &ldquo;T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng&rdquo; ph&acirc;n t&iacute;ch chiều hướng t&acirc;m l&yacute; n&agrave;y theo qu&aacute; tr&igrave;nh ph&aacute;t triển của một d&acirc;n tộc, một quốc gia. M&agrave; trong đ&oacute;, mỗi con người chịu một sự chi phối từ d&acirc;n tộc theo một c&aacute;ch v&ocirc; thức n&agrave;o đ&oacute;.</p>\r\n\r\n<p>T&acirc;m l&yacute; học đ&aacute;m đ&ocirc;ng &ndash; Gustave Le Bon</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Theo t&aacute;c giả, t&acirc;m l&yacute; của một đ&aacute;m đ&ocirc;ng bị t&aacute;c động bởi những yếu tố kh&aacute;ch quan b&ecirc;n ngo&agrave;i. Đ&oacute; l&agrave; l&yacute; do m&agrave; những đ&aacute;m đ&ocirc;ng n&agrave;y thường c&oacute; những t&acirc;m l&yacute; kh&ocirc;ng ổn định, dễ thay đổi cũng như h&agrave;nh động một c&aacute;ch v&ocirc; thức. Bạn c&oacute; thể kh&aacute;m ph&aacute; th&ecirc;m những h&igrave;nh th&aacute;i t&acirc;m l&yacute; đ&aacute;m đ&ocirc;ng trong quyển s&aacute;ch n&agrave;y.</p>\r\n\r\n<p>Xem th&ecirc;m: Top 6 cuốn s&aacute;ch thay đổi th&oacute;i quen để tạo dựng th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>5. Ch&uacute;ng ta quyết định như thế n&agrave;o? &ndash; Jonah Lehrer<br />\r\nMỗi ng&agrave;y trong cuộc sống ch&uacute;ng ta phải đưa ra rất nhiều c&aacute;c quyết định kh&aacute;c nhau, từ những quyết định đơn giản như ăn g&igrave;?, mặc g&igrave;?, &hellip; cho đến c&aacute;c quyết định quan trọng trong c&ocirc;ng việc. Nhưng bạn c&oacute; biết khi đ&oacute; n&atilde;o đ&atilde; xử l&yacute; v&agrave; hoạt động như thế n&agrave;o để t&aacute;c động đến h&agrave;nh vi ra quyết định n&agrave;y?</p>\r\n\r\n<p>Ch&uacute;ng ta quyết định như thế n&agrave;o?</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta quyết định như thế n&agrave;o&rdquo; cung cấp một c&aacute;c đầy đủ nhất những minh họa thực tế về vấn đề n&agrave;y. Th&ocirc;ng qua đ&oacute;, bạn sẽ biết được một sự thật kinh ngạc l&agrave; đ&ocirc;i khi cảm x&uacute;c sẽ gi&uacute;p bạn đưa ra được một quyết định s&aacute;ng suốt.</p>\r\n\r\n<p>Xem th&ecirc;m: 8 cuốn s&aacute;ch hay về kinh doanh đ&aacute;ng đọc nhất để th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>6. Những đ&ograve;n t&acirc;m l&yacute; thuyết phục - &nbsp;Robert B.Cialdini<br />\r\nKiểm so&aacute;t được cảm x&uacute;c của bản th&acirc;n cũng như nắm bắt được t&acirc;m l&yacute; của đối phương ch&iacute;nh l&agrave; một vũ kh&iacute; mạnh mẽ để bạn chiến thắng trong c&aacute;c hoạt động đối nh&acirc;n xử thế hằng ng&agrave;y. Nhất l&agrave; trong qu&aacute; tr&igrave;nh thuyết phục người kh&aacute;c. Với những đ&ograve;n t&acirc;m l&yacute; mạnh mẽ được gợi &yacute; từ quyển s&aacute;ch, bạn sẽ kh&ocirc;ng khỏi ngạc nhi&ecirc;n về những hiệu quả m&agrave; n&oacute; mang lại.</p>\r\n\r\n<p>Những đ&ograve;n t&acirc;m l&yacute; thuyết phục - &nbsp;Robert B.Cialdini</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Th&agrave;nh thạo về những thủ thuật t&acirc;m l&yacute; n&agrave;y, bạn chắc chắn sẽ lu&ocirc;n l&agrave;m chủ được cuộc chơi v&agrave; c&agrave;ng ng&agrave;y c&agrave;ng tạo n&ecirc;n khả năng g&acirc;y ảnh hưởng l&ecirc;n t&acirc;m l&yacute; của người kh&aacute;c.</p>\r\n\r\n<p>7. Bức x&uacute;c kh&ocirc;ng l&agrave;m ta v&ocirc; can &ndash; Đặng Ho&agrave;ng Giang<br />\r\nĐ&acirc;y được xem như l&agrave; một quyển s&aacute;ch gi&uacute;p &ldquo;lột trần&rdquo; những sự thật th&uacute; vị từ c&aacute;c sự việc th&acirc;n thuộc trong cuộc sống như c&acirc;u like, phẫu thuật thẩm mĩ, ăn thịt ch&oacute;, &hellip; Một g&oacute;c nh&igrave;n mới v&agrave; từ c&aacute;ch ph&acirc;n t&iacute;ch hấp dẫn của t&aacute;c giả sẽ gi&uacute;p bạn ngộ ra những điều chưa từng nghĩ đến li&ecirc;n quan đến c&aacute;c chủ đề th&acirc;n thuộc nhất.</p>\r\n\r\n<p>Bức x&uacute;c kh&ocirc;ng l&agrave;m ta v&ocirc; can &ndash; Đặng Ho&agrave;ng Giang</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>Từ c&aacute;c vấn đề nhỏ n&agrave;y, t&aacute;c giả c&ograve;n kh&eacute;o l&eacute;o n&acirc;ng ph&acirc;n t&iacute;ch của m&igrave;nh l&ecirc;n tầm vĩ m&ocirc; hơn. Đồng thời cung cấp c&aacute;c giải ph&aacute;p để giải quyết c&aacute;c vấn đề n&agrave;y.</p>\r\n\r\n<p>8. Phi l&yacute; tr&iacute; &ndash; Dan Ariely<br />\r\nMỗi ch&uacute;ng ta thường ra quyết định trước những vấn đề trong cuộc sống m&agrave; kh&ocirc;ng theo một nguy&ecirc;n tắc n&agrave;o cả. Khi đ&oacute;, trong t&acirc;m l&yacute; học gọi l&agrave; phi l&yacute; tr&iacute;. V&agrave; quyển s&aacute;ch n&agrave;y được ra đời để l&agrave;m g&igrave;?</p>\r\n\r\n<p>Phi l&yacute; tr&iacute; &ndash; Dan Ariely</p>\r\n\r\n<p>Mua tại Tiki Mua tại Fahasa Mua tại Shopee</p>\r\n\r\n<p>T&aacute;c giả Dan Ariely mong muốn mỗi đọc giả với những nội dung được quyển s&aacute;ch cung cấp sẽ biết c&aacute;ch đ&aacute;nh gi&aacute; lại những quyết định m&agrave; bạn đ&atilde; đưa ra. Những quyết định n&agrave;y đ&atilde; đ&uacute;ng đắn hay chưa v&agrave; c&oacute; g&igrave; cần sửa đổi? Khi đ&oacute;, bạn sẽ c&oacute; được những b&agrave;i học qu&yacute; b&aacute;u.</p>\r\n\r\n<p>9. Đọc vị bất kỳ ai &ndash; TS. David J.Lieberman<br />\r\nBạn c&oacute; từng muốn biết suy nghĩ của một ai đ&oacute; nhưng nghĩ rằng điều đ&oacute; l&agrave; kh&ocirc;ng thể. Tuy nhi&ecirc;n, bạn sẽ t&igrave;m thấy được c&aacute;c bước để thấu hiểu được ai đ&oacute; đang nghĩ g&igrave; trong những t&igrave;nh huống cụ thể. V&agrave; bạn sẽ &aacute;p dụng được trong những ho&agrave;n cảnh đơn giản. Chẳng hạn như muốn biết một ai đ&oacute; c&oacute; n&ecirc;n tin cậy hay kh&ocirc;ng, hay muốn biết một người trước đ&oacute; chưa từng gặp c&oacute; giống với những g&igrave; bạn tưởng tượng hay kh&ocirc;ng, &hellip; Bạn ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m được nhờ v&agrave;o những nội dung đắt gi&aacute; m&agrave; &ldquo;Đọc vị bất kỳ ai cung cấp&rdquo;.</p>', 2, 1, '2022-06-25 05:40:06', '2022-06-25 05:40:06'),
(14, 'Cuộc Sống “Đếch” Giống Cuộc Đời', '1656164198_rrrrrrrrrrrrrrrr_4.jpg', '1656164198_5S Bi Mat Thanh Cong Tu Nhat Ban.PDF', '<p>Sau sự th&agrave;nh c&ocirc;ng ngo&agrave;i sức tưởng tượng của cuốn s&aacute;ch đầu tay &ldquo;Cuộc sống rất giống cuộc đời&rdquo;, sau 4 năm, t&aacute;c giả Ho&agrave;ng Hải Nguyễn mới trở lại với độc giả với cuốn s&aacute;ch si&ecirc;u h&agrave;i hước &ldquo;Cuộc sống đếch giống cuộc đời&rdquo; &ndash; dự b&aacute;o một hiện tượng mạng trong thời gian tới.</p>\r\n\r\n<p>Ghi dấu ấn mạnh mẽ nhờ giọng văn ph&oacute;ng kho&aacute;ng, h&oacute;m hỉnh, s&acirc;u cay v&agrave; đặc biệt l&agrave; biệt t&agrave;i g&acirc;y cười đặc trưng c&oacute; một kh&ocirc;ng hai, chắc chắn với cuốn s&aacute;ch tiếp theo n&agrave;y, t&aacute;c giả Ho&agrave;ng Hải Nguyễn sẽ tiếp tục chinh phục c&aacute;c độc giả kh&oacute; t&iacute;nh nhất.</p>\r\n\r\n<p>Lu&ocirc;n t&acirc;m niệm m&igrave;nh l&agrave; một người viết y&ecirc;u văn chương, ch&iacute;nh v&igrave; thế m&agrave; c&aacute;c b&agrave;i viết của anh kh&ocirc;ng tạo cảm gi&aacute;c ức chế hay nh&agrave;m ch&aacute;n với những lối đi cũ m&ograve;n của văn chương hoa mĩ. Dưới g&oacute;c nh&igrave;n của một người đ&agrave;n &ocirc;ng U40, đ&atilde; c&oacute; gia đ&igrave;nh v&agrave; hai con, anh nh&igrave;n nhận sự xoay vần của cuộc đời theo c&aacute;ch của &nbsp;người từng trải c&oacute; nhiều kinh nghiệm.</p>\r\n\r\n<p>Những c&acirc;u chuyện về tổ ấm gia đ&igrave;nh, chuyện x&atilde; hội, chuyện cuộc sống, chuyện phiếm b&ecirc;n lề được kết hợp kh&eacute;o l&eacute;o với chất văn &ldquo;rất đ&agrave;n &ocirc;ng&rdquo; l&agrave; điểm s&aacute;ng gi&uacute;p anh ghi dấu trong l&ograve;ng bạn đọc. Từ những con chữ th&ocirc;ng minh, ,sắc lẹm như lưỡi dao ấy, lại khiến người đọc trăn trở, đau đ&aacute;u, c&oacute; một khoảng lặng để suy ngẫm v&agrave; nhận ra nhiều điều c&oacute; &iacute;ch.</p>\r\n\r\n<p>&ldquo;Gia đ&igrave;nh l&agrave; thứ bất khả x&acirc;m phạm. Cứ tin t&ocirc;i, mọi sự th&agrave;nh c&ocirc;ng trong x&atilde; hội đều kh&ocirc;ng thể b&ugrave; đắp được thất bại trong cuộc sống gia đ&igrave;nh&rdquo;.</p>\r\n\r\n<p>Nếu mong chờ một t&aacute;c phẩm văn học ch&iacute;nh thống với những c&acirc;u chữ kh&ocirc; khốc, th&igrave; hẳn l&agrave; cuốn s&aacute;ch sẽ khiến bạn thất vọng. Nhưng nếu bạn đang cảm thấy bế tắc trong cuộc sống, cần một ai đ&oacute; xốc lại tinh thần để tiếp tục chiến đấu với cuộc đời th&igrave; chắc chắn kh&ocirc;ng n&ecirc;n bỏ lỡ cuốn s&aacute;ch n&agrave;y. Cuộc sống đếch giống cuộc đời sẽ đem lại cho bạn những tiếng cười sảng kho&aacute;i, những ph&uacute;t gi&acirc;y thư gi&atilde;n c&ugrave;ng những b&agrave;i học s&acirc;u sắc, t&acirc;m đắc qua từng trang s&aacute;ch.</p>\r\n\r\n<p>Giống như lời t&aacute;c giả t&acirc;m sự &ldquo;Cuộc sống rất giống cuộc đời v&igrave; cuộc sống l&agrave; tập hợp của v&ocirc; số cuộc đời, cuộc sống kh&ocirc;ng giống cuộc đời v&igrave; cuộc đời l&agrave; hữu hạn c&ograve;n cuộc sống l&agrave; v&ocirc; hạn. Cho d&ugrave; cuộc sống hay cuộc đời của bạn c&oacute; kh&oacute; khan vất vả thế n&agrave;o th&igrave; t&ocirc;i vẫn mong c&aacute;c bạn h&atilde;y lu&ocirc;n suy nghĩ một c&aacute;ch t&iacute;ch cực v&agrave; h&agrave;i hước nhất.</p>\r\n\r\n<p>C&oacute; người đ&atilde; n&oacute;i rằng: Cuối c&ugrave;ng th&igrave; mọi thứ đều sẽ ổn, nếu chưa ổn th&igrave; tức l&agrave; chưa phải cuối c&ugrave;ng. Vậy đấy c&aacute;c bạn ạ!</p>\r\n\r\n<p>M&atilde; h&agrave;ng&nbsp;&nbsp; &nbsp;8936186543210<br />\r\nT&ecirc;n Nh&agrave; Cung Cấp&nbsp;&nbsp; &nbsp;AZ Việt Nam<br />\r\nT&aacute;c giả&nbsp;&nbsp; &nbsp;Ho&agrave;ng Hải Nguyễn<br />\r\nNXB&nbsp;&nbsp; &nbsp;NXB H&agrave; Nội<br />\r\nNăm XB&nbsp;&nbsp; &nbsp;2020<br />\r\nNg&ocirc;n Ngữ&nbsp;&nbsp; &nbsp;Tiếng Việt<br />\r\nTrọng lượng (gr)&nbsp;&nbsp; &nbsp;250<br />\r\nK&iacute;ch Thước Bao B&igrave;&nbsp;&nbsp; &nbsp;13 x 20.5 cm<br />\r\nSố trang&nbsp;&nbsp; &nbsp;216<br />\r\nH&igrave;nh thức&nbsp;&nbsp; &nbsp;B&igrave;a Mềm<br />\r\nSản phẩm b&aacute;n chạy nhất&nbsp;&nbsp; &nbsp;Top 100 sản phẩm Kỹ năng sống b&aacute;n chạy của th&aacute;ng<br />\r\nCuộc Sống &ldquo;Đếch&rdquo; Giống Cuộc Đời</p>\r\n\r\n<p>Sau sự th&agrave;nh c&ocirc;ng ngo&agrave;i sức tưởng tượng của cuốn s&aacute;ch đầu tay &ldquo;Cuộc sống rất giống cuộc đời&rdquo;, sau 4 năm, t&aacute;c giả Ho&agrave;ng Hải Nguyễn mới trở lại với độc giả với cuốn s&aacute;ch si&ecirc;u h&agrave;i hước &ldquo;Cuộc sống đếch giống cuộc đời&rdquo; &ndash; dự b&aacute;o một hiện tượng mạng trong thời gian tới.</p>\r\n\r\n<p>Ghi dấu ấn mạnh mẽ nhờ giọng văn ph&oacute;ng kho&aacute;ng, h&oacute;m hỉnh, s&acirc;u cay v&agrave; đặc biệt l&agrave; biệt t&agrave;i g&acirc;y cười đặc trưng c&oacute; một kh&ocirc;ng hai, chắc chắn với cuốn s&aacute;ch tiếp theo n&agrave;y, t&aacute;c giả Ho&agrave;ng Hải Nguyễn sẽ tiếp tục chinh phục c&aacute;c độc giả kh&oacute; t&iacute;nh nhất.</p>\r\n\r\n<p>Lu&ocirc;n t&acirc;m niệm m&igrave;nh l&agrave; một người viết y&ecirc;u văn chương, ch&iacute;nh v&igrave; thế m&agrave; c&aacute;c b&agrave;i viết của anh kh&ocirc;ng tạo cảm gi&aacute;c ức chế hay nh&agrave;m ch&aacute;n với những lối đi cũ m&ograve;n của văn chương hoa mĩ. Dưới g&oacute;c nh&igrave;n của một người đ&agrave;n &ocirc;ng U40, đ&atilde; c&oacute; gia đ&igrave;nh v&agrave; hai con, anh nh&igrave;n nhận sự xoay vần của cuộc đời theo c&aacute;ch của &nbsp;người từng trải c&oacute; nhiều kinh nghiệm.</p>\r\n\r\n<p>Những c&acirc;u chuyện về tổ ấm gia đ&igrave;nh, chuyện x&atilde; hội, chuyện cuộc sống, chuyện phiếm b&ecirc;n lề được kết hợp kh&eacute;o l&eacute;o với chất văn &ldquo;rất đ&agrave;n &ocirc;ng&rdquo; l&agrave; điểm s&aacute;ng gi&uacute;p anh ghi dấu trong l&ograve;ng bạn đọc. Từ những con chữ th&ocirc;ng minh, ,sắc lẹm như lưỡi dao ấy, lại khiến người đọc trăn trở, đau đ&aacute;u, c&oacute; một khoảng lặng để suy ngẫm v&agrave; nhận ra nhiều điều c&oacute; &iacute;ch.</p>\r\n\r\n<p>&ldquo;Gia đ&igrave;nh l&agrave; thứ bất khả x&acirc;m phạm. Cứ tin t&ocirc;i, mọi sự th&agrave;nh c&ocirc;ng trong x&atilde; hội đều kh&ocirc;ng thể b&ugrave; đắp được thất bại trong cuộc sống gia đ&igrave;nh&rdquo;.</p>\r\n\r\n<p>Nếu mong chờ một t&aacute;c phẩm văn học ch&iacute;nh thống với những c&acirc;u chữ kh&ocirc; khốc, th&igrave; hẳn l&agrave; cuốn s&aacute;ch sẽ khiến bạn thất vọng. Nhưng nếu bạn đang cảm thấy bế tắc trong cuộc sống, cần một ai đ&oacute; xốc lại tinh thần để tiếp tục chiến đấu với cuộc đời th&igrave; chắc chắn kh&ocirc;ng n&ecirc;n bỏ lỡ cuốn s&aacute;ch n&agrave;y. Cuộc sống đếch giống cuộc đời sẽ đem lại cho bạn những tiếng cười sảng kho&aacute;i, những ph&uacute;t gi&acirc;y thư gi&atilde;n c&ugrave;ng những b&agrave;i học s&acirc;u sắc, t&acirc;m đắc qua từng trang s&aacute;ch.</p>', '<p>Sau sự th&agrave;nh c&ocirc;ng ngo&agrave;i sức tưởng tượng của cuốn s&aacute;ch đầu tay &ldquo;Cuộc sống rất giống cuộc đời&rdquo;, sau 4 năm, t&aacute;c giả Ho&agrave;ng Hải Nguyễn mới trở lại với độc giả với cuốn s&aacute;ch si&ecirc;u h&agrave;i hước &ldquo;Cuộc sống đếch giống cuộc đời&rdquo; &ndash; dự b&aacute;o một hiện tượng mạng trong thời gian tới.</p>', 2, 1, '2022-06-25 06:36:38', '2022-06-25 06:36:38');
INSERT INTO `post` (`id_post`, `title_post`, `image_post`, `book_post`, `content_post`, `summary_post`, `id_category`, `status_post`, `created_at`, `updated_at`) VALUES
(18, 'Sức Mạnh Của Thói Quen (Power Of Habits)', '1658369826_suc_manh-cau-thoi-quen_2.jpg', '1658369827_Nhom_05.pdf', '<p>ề cơ bản, người lớn v&agrave; trẻ em kh&ocirc;ng kh&aacute;c nhau l&agrave; mấy. Bởi hầu hết những h&agrave;nh động h&agrave;ng ng&agrave;y của ch&uacute;ng ta đều l&agrave; sản phẩm của th&oacute;i quen v&ocirc; thức. Thế nhưng kh&ocirc;ng phải c&aacute; nh&acirc;n, tổ chức n&agrave;o cũng c&oacute; được th&agrave;nh c&ocirc;ng. Đ&oacute;&nbsp; l&agrave; v&igrave; mỗi người c&oacute; những th&oacute;i quen ri&ecirc;ng. Vậy th&oacute;i quen n&agrave;o mới gi&uacute;p bạn th&agrave;nh c&ocirc;ng? Trong cuốn s&aacute;ch&nbsp;<em><strong>&ldquo;Sức mạnh của th&oacute;i quen&rdquo;</strong>,</em>&nbsp;Charles Duhigg sẽ giải đ&aacute;p thắc mắc ấy.</p>\r\n\r\n<p>Ch&igrave;a kho&aacute; quan trọng nhất để mở c&aacute;nh cửa th&agrave;nh c&ocirc;ng ch&iacute;nh l&agrave; sự kết hợp nhuần nhuyễn những th&oacute;i quen tốt với nhau. C&acirc;u hỏi đặt ra l&agrave; l&agrave;m thế n&agrave;o để ph&acirc;n biệt th&oacute;i quen tốt v&agrave; th&oacute;i quen xấu? Th&oacute;i quen c&oacute; nằm trong tầm kiểm so&aacute;t của ch&uacute;ng ta hay kh&ocirc;ng? Với ba phần kh&aacute; đầy đặn, &ldquo;Sức mạnh của th&oacute;i quen&rdquo; cho bạn c&aacute;i nh&igrave;n to&agrave;n diện kh&ocirc;ng chỉ về th&oacute;i quen c&aacute; nh&acirc;n, của tổ chức m&agrave; c&ograve;n l&agrave; của to&agrave;n x&atilde; hội, c&ugrave;ng với lời khuy&ecirc;n để vận dụng c&aacute;c th&oacute;i quen đ&oacute;. Muốn thay đổi th&oacute;i quen, bạn phải ph&aacute; vỡ những việc l&agrave;m tuỳ hứng h&agrave;ng ng&agrave;y &ndash; c&acirc;u &ldquo;thần ch&uacute;&rdquo; n&agrave;y chỉ đường cho bạn tới th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Sức mạnh của th&oacute;i quen sẽ l&agrave;m bạn say m&ecirc; bởi những &yacute; tưởng th&uacute; vị, những nghi&ecirc;n cứu ấn tượng, những ph&acirc;n t&iacute;ch th&ocirc;ng minh v&agrave; những lời khuy&ecirc;n thiết thực. Những độc giả đưa cuốn s&aacute;ch n&agrave;y v&agrave;o danh s&aacute;ch bestseller của Thời b&aacute;o New York suốt 40 tuần đ&atilde; kiểm chứng điều đ&oacute;.</p>', '<p>ề cơ bản, người lớn v&agrave; trẻ em kh&ocirc;ng kh&aacute;c nhau l&agrave; mấy. Bởi hầu hết những h&agrave;nh động h&agrave;ng ng&agrave;y của ch&uacute;ng ta đều l&agrave; sản phẩm của th&oacute;i quen v&ocirc; thức. Thế nhưng kh&ocirc;ng phải c&aacute; nh&acirc;n, tổ chức n&agrave;o cũng c&oacute; được th&agrave;nh c&ocirc;ng. Đ&oacute;&nbsp; l&agrave; v&igrave; mỗi người c&oacute; những th&oacute;i quen ri&ecirc;ng. Vậy th&oacute;i quen n&agrave;o mới gi&uacute;p bạn th&agrave;nh c&ocirc;ng? Trong cuốn s&aacute;ch&nbsp;<em><strong>&ldquo;Sức mạnh của th&oacute;i quen&rdquo;</strong>,</em>&nbsp;Charles Duhigg sẽ giải đ&aacute;p thắc mắc ấy.</p>\r\n\r\n<p>Ch&igrave;a kho&aacute; quan trọng nhất để mở c&aacute;nh cửa th&agrave;nh c&ocirc;ng ch&iacute;nh l&agrave; sự kết hợp nhuần nhuyễn những th&oacute;i quen tốt với nhau. C&acirc;u hỏi đặt ra l&agrave; l&agrave;m thế n&agrave;o để ph&acirc;n biệt th&oacute;i quen tốt v&agrave; th&oacute;i quen xấu? Th&oacute;i quen c&oacute; nằm trong tầm kiểm so&aacute;t của ch&uacute;ng ta hay kh&ocirc;ng? Với ba phần kh&aacute; đầy đặn, &ldquo;Sức mạnh của th&oacute;i quen&rdquo; cho bạn c&aacute;i nh&igrave;n to&agrave;n diện kh&ocirc;ng chỉ về th&oacute;i quen c&aacute; nh&acirc;n, của tổ chức m&agrave; c&ograve;n l&agrave; của to&agrave;n x&atilde; hội, c&ugrave;ng với lời khuy&ecirc;n để vận dụng c&aacute;c th&oacute;i quen đ&oacute;. Muốn thay đổi th&oacute;i quen, bạn phải ph&aacute; vỡ những việc l&agrave;m tuỳ hứng h&agrave;ng ng&agrave;y &ndash; c&acirc;u &ldquo;thần ch&uacute;&rdquo; n&agrave;y chỉ đường cho bạn tới th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Sức mạnh của th&oacute;i quen sẽ l&agrave;m bạn say m&ecirc; bởi những &yacute; tưởng th&uacute; vị, những nghi&ecirc;n cứu ấn tượng, những ph&acirc;n t&iacute;ch th&ocirc;ng minh v&agrave; những lời khuy&ecirc;n thiết thực. Những độc giả đưa cuốn s&aacute;ch n&agrave;y v&agrave;o danh s&aacute;ch bestseller của Thời b&aacute;o New York suốt 40 tuần đ&atilde; kiểm chứng điều đ&oacute;.</p>', 2, 1, '2022-07-20 19:17:07', '2022-07-20 19:17:07');

